(def test-reports [[7 6 4 2 1], [1 2 7 8 9], [9 7 6 2 1], [1 3 2 4 5], [8 6 4 4 1], [1 3 6 7 9]])


(defn sequencia-incremental? [v]
  (let [partitioned-vector (partition 2 1 v)]
    (every? (fn [[a b]] (or
                          (= 1 (- b a))
                          (= 2 (- b a))
                          (= 3 (- b a)))) partitioned-vector)))


(sequencia-incremental? [1 3 6 7 9])

(defn sequencia-decremental? [v]
  (let [partitioned-vector (partition 2 1 v)]
    (every? (fn [[a b]] (or
                          (= 1 (- a b))
                          (= 2 (- a b))
                          (= 3 (- a b)))) partitioned-vector)))
(drop-last [1 3 2 4 5])
(drop 2 [1 3 3 4 5])
(take 2 [1 2 3 4 5])

(defn remover-por-indice [list]
  (println "list q ta chegando [remover-por-indice]: " list)
  (vec (map vec (map-indexed (fn [idx _]
                               (concat (take idx list) (drop (inc idx) list)))
                             list))))

(defn recursive-check [v]
  (println "v q ta chegando [recursive-check]: " v)
  (if (or (sequencia-incremental? v) (sequencia-decremental? v))
    true
    (map #(recursive-check %) (remover-por-indice v))))

